import { React, useState, useEffect } from "react";
import { useNavigate  } from "react-router-dom";
export default function Login() {
  
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);

  const handleShowPassword = () => {
    setShowPassword((prevShowPassword) => !prevShowPassword);
  };
  const navigate = useNavigate();
  useEffect(() => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    localStorage.removeItem("email");
  }, []);

  const _loginUser = async (e) => {
    e.preventDefault();
    let loginCredentials = {
      username: username,
      password: password,
    };
    console.log("loginCredentials",loginCredentials)
    navigate("/dashboard");
  };

  return (
    <div className="container-fluid">
      <div className="row box">
        <div className="col">
          <h2 className="page-heading">LOGIN</h2>
          {/* ... your login form and other content ... */}
          <form action="/login" method="post">
            <div className="input-box-wrap">
              <label htmlFor="username">Username:</label>
              <input type="text" id="username" name="username" required />
            </div>
            <div className="input-box-wrap">
              <label htmlFor="password">Password:</label>
              <input
                type={showPassword ? 'text' : 'password'}
                id="password"
                name="password"
                required
              />
              <i
                className="fas fa-eye password-eye-align"
                id="showPasswordIcon"
                onClick={handleShowPassword}
              ></i>
              {/* Use your preferred condition to show the error message */}
              {false && (
                <p id="error-message" className="password-error-message">
                  Incorrect username or password.
                </p>
              )}
            </div>
            <div className="btn-wrap">
              <input type="submit" value="Login" />
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};
