import React, { useState } from 'react';
import { connect } from 'react-redux';
import { addLoan } from '../actions/loanActions';
import { Link } from 'react-router-dom';
import {
  Row,
  Col,
  Button,
  Form,
  FormGroup,
  Input,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Card,
  CardBody,
  CardTitle,
  InputGroup,
} from 'reactstrap';

const LoanApplication = ({ addLoan }) => {
  const [modal, setModal] = useState(false);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [loanAmount, setLoanAmount] = useState('');
  const [loanTerm, setLoanTerm] = useState('');

  const toggle = () => {
    setModal(!modal);
  };

  const onSubmit = (e) => {
    e.preventDefault();

    const newLoan = {
      name: name,
      email: email,
      loan_amount: loanAmount,
      loan_term: loanTerm,
    };

    // Add loan via addLoan action
    addLoan(newLoan);

    // Open Modal
    toggle();
  };

  return (
<form onSubmit={_loginUser}>
      <div>
        <label htmlFor="email">Name</label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </div>
      <div className="form-group form-mod has-feedback form-field form-floating mb-2">
        <input autoComplete="off" className={"form-control default-input " + (Errors["IsEmpty_ApplicantName"] === true ? " has-err" : "")} type="text" id="legal_business_name" maxLength="128" name="legal_business_name" placeholder="Enter Applicant Name" defaultValue={legal_business_name} data-fv-field="legal_business_name" tabIndex="1" onChange={(e) => setlegal_business_name(e.target.value)} />
        {Errors["IsEmpty_ApplicantName"] === true ? <div className="has-err-msg">{Errors["ApplicantNameEmptyMsg"]}</div> : ""}
        <label className="default-label" htmlFor="legal_business_name">
          Legal Business Name
        </label>
      </div>
      <button className = "mt-6" type="submit">Login</button>
    </form>
  );
};

const mapStateToProps = state => ({
  loan: state.loan
})

export default connect(mapStateToProps, { addLoan })(LoanApplication);
