import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import {
  getLoan,
  getPaymentHistory,
  payLoan,
} from '../actions/loanActions';
import PropTypes from 'prop-types';
import {
  Row,
  Col,
  Button,
  Card,
  CardBody,
  Table,
  Form,
  FormGroup,
  Input,
  InputGroup,
  InputGroupAddon,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
} from 'reactstrap';
import { Link } from 'react-router-dom';
import Moment from 'react-moment';

const RepayLoan = ({ getLoan, payLoan, getPaymentHistory, item, match }) => {
  useEffect(() => {
    const { id } = match.params;
    getLoan(id);
    getPaymentHistory(id);
  }, [getLoan, getPaymentHistory, match.params]);

  const [modal, setModal] = useState(false);
  const [amountPaid, setAmountPaid] = useState('');

  const toggle = () => {
    setModal(!modal);
  };

  const onChange = (e) => {
    setAmountPaid(e.target.value);
  };

  const onSubmit = (e) => {
    e.preventDefault();

    const pay = {
      _id: match.params.id,
      amount_paid: amountPaid,
    };

    // Repay loan via payLoan action
    payLoan(pay);

    // Open Modal
    toggle();
    setAmountPaid('');
  };

  const { loan } = item;
  const { payments } = item;

  return (
    <div>
      <h1 className="mb-5">Repay Loan</h1>
      <Row>
        <Col>
          <Table striped>
            <thead>
              <tr>
                <th></th>
                <th>Amount Paid</th>
                <th>Balance</th>
                <th>Date Paid</th>
              </tr>
            </thead>
            <tbody>
              {payments.map(
                (
                  { _id, amount_paid, remaining_balance, date_paid },
                  index
                ) => (
                  <tr key={_id}>
                    <td>Week {index + 1}</td>
                    <td>
                      ${' '}
                      {amount_paid.toLocaleString('en-US', {
                        maximumFractionDigits: 2,
                        minimumFractionDigits: 2,
                      })}
                    </td>
                    <td>
                      ${' '}
                      {remaining_balance.toLocaleString('en-US', {
                        maximumFractionDigits: 2,
                        minimumFractionDigits: 2,
                      })}
                    </td>
                    <td>
                      <Moment format="YYYY-MM-DD HH:mm">
                        {date_paid}
                      </Moment>
                    </td>
                  </tr>
                )
              )}
            </tbody>
          </Table>
          <Link to="/all-loans" className="btn btn-outline-secondary">
            Back
          </Link>
        </Col>
        <Col>
          <Card>
            <CardBody>
              <Form onSubmit={onSubmit}>
                <FormGroup>
                  <div className="mb-2">Your Loan Balance</div>
                  ${' '}
                  {loan.loan_balance
                    ? loan.loan_balance.toLocaleString('en-US', {
                        maximumFractionDigits: 2,
                        minimumFractionDigits: 2,
                      })
                    : loan.loan_balance}
                </FormGroup>
                <FormGroup>
                  <div className="mb-2">Weekly Payment</div>
                  <h4>
                    ${' '}
                    {loan.weekly_payment
                      ? loan.weekly_payment.toLocaleString('en-US', {
                          maximumFractionDigits: 2,
                          minimumFractionDigits: 2,
                        })
                      : loan.weekly_payment}
                  </h4>
                </FormGroup>
                <hr className="my-4" />
                <FormGroup>
                  <div className="mb-2">Amount to Pay</div>
                  <InputGroup size="lg">
                    <div className="input-group-prepend">
                      <span className="input-group-text">$</span>
                    </div>
                    <Input
                      placeholder="Enter Amount"
                      type="number"
                      name="amount_paid"
                      id="amount_paid"
                      onChange={onChange}
                    />
                  </InputGroup>
                </FormGroup>
                <FormGroup className="mt-4">
                  <Button color="success" size="lg" block type="submit">
                    Submit
                  </Button>
                </FormGroup>
              </Form>
            </CardBody>
          </Card>
        </Col>
      </Row>

      <Modal isOpen={modal} toggle={toggle}>
        <ModalHeader toggle={toggle}>Payment Successful</ModalHeader>
        <ModalBody>Thank you for your payment!</ModalBody>
        <ModalFooter>
          <Button color="primary" onClick={toggle}>
            Done
          </Button>{' '}
        </ModalFooter>
      </Modal>
    </div>
  );
};

RepayLoan.propTypes = {
  getLoan: PropTypes.func.isRequired,
  payLoan: PropTypes.func.isRequired,
  getPaymentHistory: PropTypes.func.isRequired,
  item: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  item: state.item,
});

export default connect(mapStateToProps, {
  getLoan,
  payLoan,
  getPaymentHistory,
})(RepayLoan);
