import React, { useState } from 'react';
import { connect } from 'react-redux';
import { addLoan } from '../actions/loanActions';
import { Link } from 'react-router-dom';
import {
  Row,
  Col,
  Button,
  Form,
  FormGroup,
  Input,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Card,
  CardBody,
  CardTitle,
  InputGroup,
} from 'reactstrap';

const LoanApplication = ({ addLoan }) => {
  const [modal, setModal] = useState(false);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [loanAmount, setLoanAmount] = useState('');
  const [loanTerm, setLoanTerm] = useState('');

  const toggle = () => {
    setModal(!modal);
  };

  const _Submit = (e) => {
    e.preventDefault();

    const newLoan = {
      name: name,
      email: email,
      loan_amount: loanAmount,
      loan_term: loanTerm,
    };

    // Add loan via addLoan action
    addLoan(newLoan);

    // Open Modal
    toggle();
  };

  return (
<form>
      <div>
        <label htmlFor="email">Name</label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </div>
      <div className="form-group form-mod has-feedback form-field form-floating mb-2">
        <input autoComplete="off" className={"form-control default-input " + (Errors["IsEmpty_ApplicantName"] === true ? " has-err" : "")} type="text" id="email" maxLength="128" name="email" placeholder="Enter Email Address" defaultValue={email} data-fv-field="email" tabIndex="1" onChange={(e) => setEmail(e.target.value)} />
        {Errors["IsEmpty_ApplicantName"] === true ? <div className="has-err-msg">{Errors["ApplicantNameEmptyMsg"]}</div> : ""}
        <label className="default-label" htmlFor="email">
          Email
        </label>
      </div>
      <div className="buttons button_space  mt-3">
        <button type="button" className="back_button px-3" onClick={goback}>
          Go Back
        </button>
        <button type="button" className="next_button" onClick={_Submit}>
          Submit
        </button>
      </div>
    </form>
  );
};


export default LoanApplication
