import { React, useState, useEffect } from "react";
import { useHistory } from "react-router-dom";

export default function Login() {
  
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [ErrorMsg, setErrorMsg] = useState("");
  const [IsError, setIsError] = useState("");
  const hi = useHistory();
  useEffect(() => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    localStorage.removeItem("email");
  }, []);

  const _loginUser = async (e) => {
    e.preventDefault();
    let loginCredentials = {
      username: username,
      password: password,
    };
    console.log("loginCredentials",loginCredentials)
    hi.push("/dashboard");
  };

  return (
    <>
      <div className="container-fluid p-0 bg">
        <div className="row box align-items-center justify-content-center">
          <div className="">
            <div className="img-outer">
              <div className="img-wrap-right text-center">
                <h2 className="login-header-h2">Login</h2>
              </div>
            </div>

            <div className="login">
              <div className="form card">
                <form
                  id="account"
                  action="/login"
                  method="post"
                  className="login-form text-center"
                >
                  <div className="single-input">
                    <span className="login-input-icon">
                      <i className="fas fa-user"></i>
                    </span>
                    <input
                      type="email"
                      data-val="true"
                      data-val-email="The Email field is not a valid e-mail address."
                      data-val-required="The Email field is required."
                      id="Input_Email"
                      name="username"
                      value={username}
                      onChange={(e) => setUsername(e.target.value)}
                    />
                    <span
                      className="text-danger field-validation-valid"
                      data-valmsg-for="Input.Email"
                      data-valmsg-replace="true"
                    ></span>
                  </div>
                  <div className="single-input">
                    <span className="login-input-icon">
                      <i className="fas fa-unlock"></i>
                    </span>

                    <input
                      type="password"
                      data-val="true"
                      data-val-required="The Password field is required."
                      id="Input_Password"
                      name="password"
                      onChange={(e) => setPassword(e.target.value)}
                      value={password}
                    />
                    <span
                      className="text-danger field-validation-valid"
                      data-valmsg-for="Input.Password"
                      data-valmsg-replace="true"
                    ></span>
                  </div>
                  {IsError === true && (
                    <>
                      <div
                        className="alert alert-danger fade show"
                        role="alert"
                      >
                        <p className=" mb-0 text-start">{ErrorMsg}</p>
                      </div>
                    </>
                  )}
                  <div className="text-center d-block forgot-btm-border pb-3">
                    <button
                      type="submit"
                      className="btn btn-lg sign-in mb-2"
                      onClick={_loginUser}
                    >
                      Log in
                    </button>
                    <br />
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
